(1131, '2300', 'Using the Standby Hydraulic power for thrust reverser deployment will cause', 'Deployment time not to be affected.', 'Deployment time to increase.', 'Deployment time to decrease.', NULL, 'b', 'Deployment time to increase.', 'B-737', 'hydraulics_b-737', 'pilot'),
(1132, '2301', 'The standby system low quantity light is', 'Armed at all times and will illuminate whenever the reservoir is approximately half empty.', 'Armed at all times and will illuminate whenever the reservoir is approximately three quarters empty.', 'Armed only when either Flt Control switch is placed to the STBY RUD position.', NULL, 'a', 'Armed at all times and will illuminate whenever the reservoir is approximately half empty.', 'B-737', 'hydraulics_b-737', 'pilot'),
(1133, '2302', 'The Standby hydraulic fluid', 'Is cooled by a heat exchanger located in NO. 2 tank.', 'Is cooled by a heat exchanger located in NO. 1 tank.', 'Is not cooled.', NULL, 'c', 'Is not cooled.', 'B-737', 'hydraulics_b-737', 'pilot'),
