(1138, '2307', 'With any control cabin window heating inoperative, speed should be restricted to', '250 Kts at altitudes below 15,000ft.', '280 Kts at altitudes below 15,000ft.', '250 Kts at altitudes below 10,000ft.', NULL, 'c', '250 Kts at altitudes below 10,000ft.', 'B-737', 'ice_&_rain_protection_b-737', 'pilot'),
(1139, '2308', 'If a No.1 window outer pane cracks', 'Turn window heat off, limit IAS to 250kts below 10,000 ft.', 'Cabin differential pressure must be reduced to a maximum of 2 PSI', 'Cabin differential pressure must be reduced to a maximum of 5 PSI', NULL, 'a', 'Turn window heat off, limit IAS to 250kts below 10,000 ft.', 'B-737', 'ice_&_rain_protection_b-737', 'pilot'),
